mapper.insert:
insert into contest(
code, username, date, problem_id ,language
)
values (
#{code},#{username},#{date},#{problemId},#{language}
)

mapper.select:
select id, username, date, problem_id, result, time, memory, length ,language
from submissions


        username = #{username}


        problem_id = #{problemId}


        result = #{result}


        language = #{language}


ORDER BY id DESC
